import Particle from './Particle';
import { asteroidVertices, randomNumBetween } from './helpers';
import { addInterval, removeInterval } from './gameIntervalHandler'
import type {
  CanvasItem,
  IState,
  Iposition,
  IupgradeBase,
  IshipWeapon,
  IsecondaryWeapon,
  IshipEquipment,
  IgameChanger,
  IspaceInterferer,
  upgradeArray,
} from './game.types'

export interface Iprops {
  position: Iposition,
  size: number,
  create:Function,
  addScore: Function,
  onSound: Function,
  upgradeType: number,
  upgrade: Function,
}

export interface IupgradeType {
  type: string,
  size: number,
  duration: number,
  image: string,
  color: string,
}

export default class Present {
  type;
  position;
  vertices;
  rotation;
  score;
  velocity:Iposition;
  create;
  addScore;
  onSound;
  radius;
  rotationSpeed;
  delete;
  image:HTMLImageElement;
  alpha;
  public isInRadar;
  upgrade;
  upgradeTypes:upgradeArray;
  upgradeType: number;
  color;
  lifeSpan:number;

  getUpgrade = ():IshipWeapon|IshipEquipment|IgameChanger|IspaceInterferer =>
    this.upgradeTypes[this.upgradeType]

  constructor(props:Iprops) {
    this.type = 'present'
    this.position = props.position
    this.velocity = {
      x: randomNumBetween(-1.5, 1.5),
      y: randomNumBetween(-1.5, 1.5)
    }
    this.rotation = 0;
    this.delete = false;
    this.rotationSpeed = randomNumBetween(-1, 1)
    this.radius = props.size;
    this.score = Math.floor((80/this.radius)*5);
    this.create = props.create;
    this.addScore = props.addScore;
    this.vertices = asteroidVertices(8, props.size)
    this.alpha = 1;
    this.onSound = props.onSound;
    this.upgrade = props.upgrade
    this.isInRadar = false
    this.lifeSpan = randomNumBetween(800, 2000),

    this.upgradeTypes = [
      {
        type: 'extraLife',
        image: 'data:image/svg+xml;base64,',
        color: '#ff00dd',
      } as IgameChanger,
      {
        type: 'nova',
        image: 'data:image/svg+xml;base64,',
        color: '#9502fe',
        catchSound: 'none',
      } as IspaceInterferer,
      {
        type: 'biggerBullets',
        size: 15,
        duration: 700,
        image: 'data:image/svg+xml;base64,',
        color: '#ff3131',
        range: 400,
        lastShotLimit: 170,
        speed: 200
      } as IshipWeapon,
      {
        type: 'triple',
        size: 2,
        duration: 700,
        image: 'data:image/svg+xml;base64,',
        color: '#ff3131',
        range: 400,
        lastShotLimit: 170,
        speed: 200
      } as IshipWeapon,
      {
        type: 'speedShot',
        size: 2,
        duration: 700,
        image: 'data:image/svg+xml;base64,',
        color: '#773131',
        range: 1000,
        lastShotLimit: 170,
        speed: 200
      } as IsecondaryWeapon,

      {
        type: 'lazar',
        size: 2,
        duration: 400,
        image: 'data:image/svg+xml;base64,',
        color: '#ff3131',
        range: 600,
        lastShotLimit: 0,
      } as IshipWeapon,
      /*
      {
        type: 'shield',
        duration: 1000,
        image: 'data:image/svg+xml;base64,',
        color: '#e8a634',
      } as IshipEquipment,
      */
      {
        type: 'autoShield',
        duration: 1000,
        image: 'data:image/svg+xml;base64,',
        color: '#e8a634',
      } as IshipEquipment, 
      {
        type: 'noinfinity',
        image: 'data:image/svg+xml;base64,',
        color: '#9502fe',
        catchSound: 'none',
      } as IspaceInterferer,
      
    ]
    this.upgradeType = props.upgradeType || 0;

    this.image = new Image();
    this.image.src = this.upgradeTypes[this.upgradeType].image;
    this.color=this.upgradeTypes[this.upgradeType].color || '#ffc131';
    this.playShowUpSound()
  }
  playShowUpSound():void {
    this.onSound({
      file: 'upgradeShowUp',
      status: 'PLAYING'
    })
  }

  inRadar() {
    //this.playShowUpSound()
  }
  changeAlpha(direction:string) {
    /*
    if (this.alpha < 1 && direction === "+" ) {
      this.alpha = this.alpha + 0.02;
    }
    if (this.alpha > 0.1 && direction === "-" ) {
      this.alpha = this.alpha - 0.02;
    }
    */
  }

  selfDestruction():void {
    this.delete = true;
    // Explode
    for (let i = 0; i < this.radius; i++) {
      const particle = new Particle({
        lifeSpan: randomNumBetween(15, 30),
        size: randomNumBetween(1, 4),
        position: {
          x: this.position.x + randomNumBetween(-this.radius/4, this.radius/4),
          y: this.position.y + randomNumBetween(-this.radius/4, this.radius/4)
        },
        velocity: {
          x: randomNumBetween(-1.5, 1.5),
          y: randomNumBetween(-1.5, 1.5)
        }
      });
      this.create(particle, 'particles');
    }
  }

  destroy(byWho:string):void {
    this.delete = true;
    if (this.upgradeTypes[this.upgradeType].catchSound !== 'none') {
      this.onSound({
        file: 'upgradeCatch',
        status: 'PLAYING'
      })
    }
    this.addScore(this.score);

  }

  render(state:IState, ctx:any):void{

    
    // Destroy with effects if max age has excided
    if (this.lifeSpan-- < 0){
      this.selfDestruction()
    }
    
    // Move
    this.position.x += this.velocity.x;
    this.position.y += this.velocity.y;

    // Rotation
    this.rotation += this.rotationSpeed;
    if (this.rotation >= 360) {
      this.rotation -= 360;
    }
    if (this.rotation < 0) {
      this.rotation += 360;
    }

    // Screen edges
    /*
    if(this.position.x > state.screen.width + this.radius) this.position.x = -this.radius;
    else if(this.position.x < -this.radius) this.position.x = state.screen.width + this.radius;
    if(this.position.y > state.screen.height + this.radius) this.position.y = -this.radius;
    else if(this.position.y < -this.radius) this.position.y = state.screen.height + this.radius;
    */

    // Screen edges
    if (state.inifityScreen) {
      if (this.position.x > state.screen.width + this.radius) {
        this.position.x = -this.radius;
      } else if(this.position.x < -this.radius){
        this.position.x = state.screen.width + this.radius;
      }
      if (this.position.y > state.screen.height + this.radius) {
          this.position.y = -this.radius;
      } else if (this.position.y < -this.radius) {
        this.position.y = state.screen.height + this.radius;
      }
    } else {

      let x = this.velocity.x
      let y = this.velocity.y
      
      if (this.position.x + this.radius> state.screen.width) {
        x = -this.velocity.x;
      } else if(this.position.x - this.radius < 0){
        x = -this.velocity.x
      }
      if (this.position.y + this.radius > state.screen.height) {
        y = -this.velocity.y;
      } else if (this.position.y - this.radius < 0) {
        y = -this.velocity.y
      }

      const newVelocity = {x,y}
      this.velocity = newVelocity
    }

    // Draw
    const context = ctx
    if (context) {
      context.save();
      context.translate(this.position.x, this.position.y);
      context.rotate(this.rotation * Math.PI / 180);
      context.strokeStyle = this.color;
      context.fillStyle = this.color // ef404f
      context.lineWidth = 2;
      //context.globalAlpha = this.alpha;
      context.beginPath();
      context.arc(0, 0, 20, 0, 2 * Math.PI, false);
      context.stroke();
      context.fill();
      context.closePath();
      context.clip();
      context.drawImage(this.image, this.radius / 2 * (-1), this.radius / 2 * (-1), 20, 20);
      context.restore();
    }
  }
}
